package org.example.minitest.service;

import org.example.minitest.model.Book;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class IBookService implements BookService{
    private static Map<Integer, Book> books;
    static {
        books = new HashMap<>();
        books.put(1, new Book(1,"book1","author1",1000,"data:image/jpeg;base64,"));
        books.put(2, new Book(2,"book2","author2",1000,"data:image/jpeg;base64,"));
        books.put(3, new Book(3,"book3","author3",1000,"data:image/jpeg;base64,"));
        books.put(4, new Book(4,"book4","author4",1000,"data:image/jpeg;base64,"));
    }
    @Override
    public List<Book> showAll() {
        return new ArrayList<>(books.values());
    }

    @Override
    public void saveList(Book book) {
        books.put(book.getId(), book);
    }

    @Override
    public Book searchById(int id) {
        return books.get(id);
    }

    @Override
    public void update(int id, Book book) {
        books.put(id, book);
    }

    @Override
    public void delete(int id) {
        books.remove(id);
    }
}
